export default function fbpubfletcher32(inputData) {
    var data = new DataView(inputData);
    var sum1 = 0xffff;
    var sum2 = 0xffff;
    var wordsRemaining = data.byteLength >>> 1;
    var currentOffset = data.byteLength & 0x1;
    if (currentOffset)
        sum1 += data.getUint8(0);
    while (wordsRemaining) {
        var tlen = Math.min(wordsRemaining, 358);
        wordsRemaining -= tlen;
        for (; tlen; tlen -= 1, currentOffset += 2) {
            sum1 += data.getUint16(currentOffset, false);
            sum2 += sum1;
        }
        sum1 = (sum1 & 0xffff) + (sum1 >>> 16);
        sum2 = (sum2 & 0xffff) + (sum2 >>> 16);
    }
    sum1 = (sum1 & 0xffff) + (sum1 >>> 16);
    sum2 = (sum2 & 0xffff) + (sum2 >>> 16);
    return ((sum2 << 16) | sum1) >>> 0;
}
