import { memoize } from './Schemable';
// -------------------------------------------------------------------------------------
// constructors
// -------------------------------------------------------------------------------------
/**
 * @category constructors
 * @since 2.2.0
 */
export function make(schema) {
    return memoize(schema);
}
/**
 * @since 2.2.3
 */
export var interpreter = function (S) { return function (schema) { return schema(S); }; };
